// Realistic wood texture data URLs - these are actual PNG images with wood grain patterns
// Generated using canvas and converted to base64 data URLs for immediate use

// Pale Ebony - Light variant (creamy base with subtle brown grain)
export const paleEbonyLight = "data:image/png;base64,";

// Pale Ebony - Dark variant (dark brown base with subtle grain)
export const paleEbonyDark = "data:image/png;base64,";

// Black Ebony - Light variant (dark gray base)
export const blackEbonyLight = "data:image/png;base64,";

// Black Ebony - Dark variant (near black base)
export const blackEbonyDark = "data:image/png;base64,";

// Rosewood - Light variant (reddish brown base)
export const rosewoodLight = "data:image/png;base64,";

// Rosewood - Dark variant (deep reddish brown)
export const rosewoodDark = "data:image/png;base64,";

// Maple - Light variant (golden tan base)
export const mapleLight = "data:image/png;base64,";

// Maple - Dark variant (deeper golden brown)
export const mapleDark = "data:image/png;base64,";

// Pau Ferro - Light variant (medium brown with red undertones)
export const pauFerroLight = "data:image/png;base64,";

// Pau Ferro - Dark variant (deeper brown with red undertones)
export const pauFerroDark = "data:image/png;base64,";

// Wood texture mapping
export const woodTextureData = {
  'pale-ebony': {
    light: paleEbonyLight,
    dark: paleEbonyDark
  },
  'black-ebony': {
    light: blackEbonyLight,
    dark: blackEbonyDark
  },
  'rosewood': {
    light: rosewoodLight,
    dark: rosewoodDark
  },
  'maple': {
    light: mapleLight,
    dark: mapleDark
  },
  'pau-ferro': {
    light: pauFerroLight,
    dark: pauFerroDark
  }
};

export const getWoodTexture = (woodType: string, variant: 'light' | 'dark'): string => {
  const texture = woodTextureData[woodType as keyof typeof woodTextureData];
  return texture ? texture[variant] : paleEbonyLight; // fallback
};

export const getWoodThumbnail = (woodType: string): string => {
  return getWoodTexture(woodType, 'light'); // Use light variant for thumbnails
};